  value={fullName.fname}/>

  <input type="password" 
  placeholder='Enter Your password'
  name='lname'
 onChange={inputEvent}
  value={fullName.lname}
  />
  <button type='submit'>Click Me</button>
  </div>

  </>
)
}

export default App;
